     -        blankline,mmtype,ibnd,index,itemp4,indexo,indexs,indexdel,
     -        indexa,mmtype,indexsup,atw,edge,ifree,namin,namout,resin,
     -        resout,icntrl,icntrlw,iha,molresflag,altcol,inscol,
     -        ninsres,temp,rprox,irescount3,itemp1,hblimfac,angmin,
     -        irepav,ncl,nextconfsel,molsltlim,nmolslt,nmolsltnoion,1,
     -        minresflag,iqspaceask,0,iwriteatsym,radtodeg,tofac,
     -        irepcall,ipbcinp,1,lentest_ok,maxconfsel,maxrepconf,pi,
     -        MAXRSD,MAXNEIG,MAXREC)
          else
            innlist=-1
            nextconfsel=1
            ionefile=0
            imodel=0
            if (ans .eq. 'o' .or. ans .eq. 'm') ionefile=1
            if (ans .eq. 'm') imodel=1
            if (ionefile .eq. 0) call askyn(
     -        'Do you want to number the files consecutively',45,
     -        1,0,numconsec,1,0)
            if (imodel .eq. 0) then
              call getcrdtyp(0,'   ',3,iotyp,ixcl,'output',6)
            else
              iotyp=3
            end if
            write (6,2016) formatname(iotyp)
            separatorchar='.'
            call unpacktraj(inptrajtyp,mmctrajtyp,inpcrdtyp,iotyp,
     -        inpcrdtyporg,n,nslt,naslv,iislvw,numres,numslv,resnamslv,
     -        iatnum,ifchrg,ixres,atnames,resnames,segnames,altcol,
     -        inscol,ninsres,marker,ireseq,iresnrestart,iresidrestart,c,
     -        rprox,cv,charge,ntitlin,title,nneig,nneiga,nhbneig,ineig,
     -        nhneig,nnneig,ncneig,nsneig,npneig,isegno,iresno,line,
     -        blankline,ifree,icntrl,innlist,mmtype,ibnd,index,indexn,
     -        indexo,molresflag,hblimfac,angmin,ioppbc,cell,ncell,
     -        ionefile,imodel,ncl,irepav,irescount3,itemp1,
     -        nclstmem,numconsec,incr_fileno,nextconfsel,iqspaceask,
     -        keeprem,radtodeg,ioverallconf,lentest_ok,maxconfsel,
     -        maxrepconf,MAXNEIG,MAXRSD,MAXREC,MAX2D)
          end if
        end if
      end if
      if (nrotwarn .gt. 0) write (6,1213) nrotwarn,nrottest,devmax
      if (itraj .gt. 0) stop
      go to 9910
c-----(11) Generate Amsol input
9100  call makeamsol(n,c,iatnum,nneig,ineig,iqcol1,iqcol2,line,
     -  index,indexn,inpcrdtyp,nconfig,iuout,MAXNEIG,MAXREC)
      go to 91
c-----(12),(37) Generate trajectory from input configuration sequence
9200  if (nconfig .eq. 1) then
        nastart=n
        if (anspup .eq. 'm') then
          if (ispdb(inpcrdtyp) .eq. 0) then
            print *,'MODEL/ENDMDL file can only be made from PDB files'
            stop
          else
            print *,'Input PDB files are assumed to be separate,',
     -        ' one structure each'
            iaddfile=1
            nsetfile=1
          call openfile(iuout,0,'output MODEL/ENDMDL',19,'new',
     -      outfile,namleno,notfnd,0,1,1,0,0)
          end if
        end if
        namleno=0
        if (ioutrajtyp .eq. 1 .or.
     -     (ioutrajtyp .eq. 3 .and. mmctrajtyp .eq. 1)) then
          call openfile(iuout,0,'output trajectory',17,'new',
     -      outfile,namleno,notfnd,0,2,1,0,0)
        else if (ioutrajtyp .gt. 0) then
          call openfile(iuout,0,'output trajectory',17,'new',
     -      outfile,namleno,notfnd,0,1,1,0,0)
        end if
        if (notfnd .gt. 0) then
          print *,'ERROR: problem opening file ',outfile(1:namleno)
          stop
        end if
        call indexit(indexs,1,n,0)
        call getint('Number of configurations to combine',35,0,1,0,
     -    nconftot,0)
        nconfadd=nconftot-1
        if (nconfadd .gt. 0) then
          if (anspup .ne. 'm') call askyn('Are they in separate files',
     -      26,1,+1,iaddfile,43,0)
          if (iaddfile .eq. 1) then
            call filenamenum(inpfile,namleni,outfiletmp,namlenn,2,-1)
            write (6,2048)
            call writeline(6,outfiletmp,1,namlenn,0)
            write (6,2049)
            if (anspup .ne. 'm') call getint(
     -        'Number of datasets per file',27,1,1,nconfadd,nsetfile,0)
          end if
        end if
        if (ntitltr .lt. 30) then
          trtitle(ntitltr+1)=
     -      'Simulaid combined single structures form file(s)'
          trtitle(ntitltr+2)=inpfile(1:namleni)
          ntitltr=ntitltr+2
        end if
        if (ietotread .eq. 1) then
          call getreal('Energy threshold (max) for filtering',36,0.0,
     -      etotfilt,0,0)
          if (ntitltr .lt. 30) then
            write (trtitle(ntitltr+1)(1:41),2128) etotfilt
            ntitltr=ntitltr+1
          end if
          call askyn('Do you want to sort the structures by energy',44,
     -      1,-1,isortbye,6,0)
          if (isortbye .eq. 1) then
            print *,'N=',n,' MAXREC10=',maxrec10,' MAX2D=',MAX2D
            maxsort=min0((maxrec10-n)/n+1,MAX2D)
            write (6,2129) maxsort
            if (ntitltr .lt. 30) then
              write (trtitle(ntitltr+1)(1:63),2129) maxsort
              ntitltr=ntitltr+1
            end if
          end if
        end if
      end if
      if (anspup .eq. 'm') then
        write (iuout,2043) nconfig
        rewind 10
        do while (.true.)
          call blankout(liner,1,80)
          read (10,1000,end=9201) liner
          if (liner(1:3) .eq. 'END') then
            write (iuout,2044)
            go to 9201
          else
            call lastchar(liner,lc,80)
            write (iuout,1000) liner(1:lc)
          end if
        end do
9201    if (liner(1:3) .ne. 'END') write (iuout,2044)
      else
        iskip=0
        if (ietotread .eq. 1) then
          if (etotread .lt. etotfilt) then
            etotsum=etotsum+etotread
            etotsum2=etotsum2+etotread**2
            if (etotread .lt. etotmin) etotmin=etotread
            if (etotread .gt. etotmax) etotmax=etotread
            netot=netot+1
          else
            neskip=neskip+1
            iskip=1
          end if
        end if
        if (iskip .eq. 0) then
          if (isortbye .eq. 0) then
            call maketraj(nconfig,nconfadd+1,c,c1,rprox,cv,ih,nslt,n,
     -        naslv,islvw,ntitltr,trtitle,line,altcol,inscol,ninsres,
     -        inpcrdtyp,1,inptrajtyp,ioutrajtyp,mmctrajtyp,20,iatnum,
     -        ifchrg,innlist,iresno,ixres,atnames,resnames,segnames,
     -        charge,isegno,marker,ntitlin,title,ireseq,iresnrestart,
     -        iresidrestart,nneig,nneiga,nhbneig,ineig,nhneig,nnneig,
     -        ncneig,nsneig,npneig,numres,numslv,resnamslv,blankline,
     -        mmtype,ibnd,index,indexn,indexo,indexs,icntrlw,icntrl,
     -        ifree,molresflag,irescount3,itemp1,0,0,0,n,hblimfac,
     -        angmin,iqspaceask,keeprem,iwriteatsym,radtodeg,etotread,
     -        0,maxrepconf,MAXNEIG,MAXRSD,MAXREC)
          else
c           Save the coordinates
            if (nastart .ne. n) then
              print *,'Sorry, sorting is not implemented for variable ',
     -          'system sizes'
              stop
            end if
            nesaved=nesaved+1
            if (nesaved .gt. maxsort) then
              write (6,2130) maxsort
              stop
            end if
            call trnsfr(co(1,nasum+1),c,3*n)
            etotsort(nesaved)=etotread
            nasum=nasum+n
            itemp5(nesaved)=nconfig
          end if
        end if
      end if
      if (nconfadd+1 .gt. nconfig) then
        if (iaddfile .eq. 1 .and. mod(nconfig,nsetfile) .eq. 0) then
          close (10)
          numfil=nconfig/nsetfile+1
          call filenamenum(inpfile,namleni,outfiletmp,namlenn,numfil,-1)
          call openfile(10,0,'input',5,'old',outfiletmp,namlenn,
     -      notfnd,0,1,1,0,0)
        end if
      else if (isortbye .eq. 1) then
c       Now sort and print the structures
        call indexit(indx2esort,1,nesaved,0)
        call mrgsrt(6,indx2esort,etotsort,nesaved,itemp2,itemp3,itemp4,
     -    temp,maxsort)
        print *,'Original indices and energies are written to file ',
     -    molfilelog(1:lmolfilelog)
        do i=1,nesaved
          write (mflogfile,2131) i,itemp5(indx2esort(i)),etotsort(i)
          call trnsfr(c,co(1,(indx2esort(i)-1)*n+1),3*n)
          call maketraj(i,nesaved,c,c1,rprox,cv,ih,nslt,n,
     -      naslv,islvw,ntitltr,trtitle,line,altcol,inscol,ninsres,
     -      inpcrdtyp,1,inptrajtyp,ioutrajtyp,mmctrajtyp,20,iatnum,
     -      ifchrg,innlist,iresno,ixres,atnames,resnames,segnames,
     -      charge,isegno,marker,ntitlin,title,ireseq,iresnrestart,
     -      iresidrestart,nneig,nneiga,nhbneig,ineig,nhneig,nnneig,
     -      ncneig,nsneig,npneig,numres,numslv,resnamslv,blankline,
     -      mmtype,ibnd,index,indexn,indexo,indexs,icntrlw,icntrl,
     -      ifree,molresflag,irescount3,itemp1,0,0,0,n,hblimfac,
     -      angmin,iqspaceask,keeprem,iwriteatsym,radtodeg,
     -      etotsort(i),0,maxrepconf,MAXNEIG,MAXRSD,MAXREC)
        end do
      end if
      go to 9910
c-----(13) Rearrange atoms according to an RTF file
9300  if (nconfig .eq. 1)
     -  call getconvdat(1,5,2,nconvdat,iconvtyp,n,line,index,
     -    inamcol1,inamcol2,nnamcol,irescol1,irescol2,igrpinfo,MAXREC)
      call sortatresrtf(line,n,nslt,index,indexn,indexo,isegno,itemp1,
     -  irescol1,irescol2,iresncol1,iresncol2,isegcol1,isegcol2,
     -  inamcol1,inamcol2,inpcrdtyp,nconfig,MAXREC)
c     print *,'sortrtf done'
      go to 999
c-----(14),(15) Permute coordinates or rotate/translate structure
9400  if (iruntyp .eq. 14) then
c-------Permute coordinates
        if (nconfig .eq. 1) then
c         Specify permutation
          ixyzprod=1
          do while (ixyzprod .ne. 6)
            do k=1,3
              linewr(1:25)='New index of coordinate '//xyz(k)
              call getint(linewr,25,k,1,3,ixyzperm(k),000)
              ixyzprod=ixyzprod*ixyzperm(k)
            end do
            if (ixyzprod .ne. 6) then
              print *,'Invalid index set'
              ixyzprod=1
            end if
          end do
          do ia=1,n
            call trnsfr(c0,c(1,ia),3)
            do k=1,3
              c(ixyzperm(k),ia)=c0(k)
            end do
          end do
        end if
      else
c-------Rotate/translate structure
        call transrot(c,c2,atw,temp,rot,xyzmin,c0,edge_gen,molsltlim,
     -    nmolslt,nmolsltnoion,minresflag,n,nslt,naslv,npbc,ixyzhex,
     -    nneig,ineig,iatnum,molresflag,ixres,atnames,latnam,resnames,
     -    lresnam,iresno,indexn,indexo,indexa,1,nconfig,nosegid,pi,
     -    MAXRSD,MAXNEIG,MAXREC)
      end if
      if (inpcrdtyp .eq. iomol2) then
c       Write coordinates back and save the mol2 file (temporary hack)
        do ia=1,nslt
          write (line(index(ia))(iccol1:iccol2),1107) (c(k,ia),k=1,3)
        end do
        do i=1,nlines
          call lastchar(line(i),lc,80)
          write (iuout,1000) line(i)(1:lc)
        end do
        stop
      end if
      call writeconf(iuout,inpcrdtyp,iotyp,inpcrdtyporg,n,n,nslt,naslv,
     -  islvw,iasv,namesv,qsv,pflsv,1,iwhead,0,iatnum,ifchrg,nconfig,
     -  innlist,c,rprox,cv,ixres,iresno,atnames,resnames,segnames,
     -  charge,isegno,altcol,inscol,ninsres,marker,ntitlin,ntitlinw,
     -  title,ireseq,iresnrestart,iresidrestart,nneig,nneiga,nhbneig,
     -  ineig,nhneig,nnneig,ncneig,nsneig,npneig,numres,numslv,
     -  resnamslv,line,blankline,mmtype,ibnd,index,indexn,indexo,0,
     -  molresflag,irescount3,itemp1,hblimfac,angmin,0,nosort,noreg,0,0,
     -  5,iqspaceask,0,0,0.0,0,0,0,keeprem,iwriteatsym,radtodeg,
     -  maxrepconf,MAXNEIG,MAXRSD,MAXREC)
      stop
c-----(16) Extract parts of the system
9600  call select(line,nrecdel,idcol,asterisk,n,nslt,index,ixres,
     -  isegcol1,isegcol2,iseqncol1,iseqncol2,inamcol1,inamcol2,
     -  irescol1,irescol2,iqcol1,iqcol2,charge,iatnum,nneig,ineig,
     -  indexdel,0,MAXNEIG,MAXREC)
      go to 999
c-----(17) Convert trajectories
9700  call convtraj(ioutrajtyp,inptrajtyp,mmctrajtyp,inpcrdtyp,n,nslt,
     -  naslv,islvw,iatnum,ifchrg,innlist,ih,c,co,c1,c2,rprox,cv,iresno,
     -  ixres,ifres,ilres,itemp1,itemp2,itemp3,atnames,resnames,
     -  segnames,charge,isegno,itemp5,segid4,irescount1,irescount2,
     -  marker,asterisk,ntitlin,title,ireseq,iresnrestart,iresidrestart,
     -  inamcol1,inamcol2,irescol1,irescol2,iseqncol1,iseqncol2,
     -  isegcol1,isegcol2,iqcol1,iqcol2,idcol,nneig,nneiga,nhbneig,
     -  ineig,nhneig,nnneig,ncneig,nsneig,npneig,numres,numslv,
     -  resnamslv,line,blankline,mmtype,ibnd,index,indexn,indexo,indexs,
     -  indexdel,indexa,mmtype,indexsup,atw,edge,ifree,namin,namout,
     -  resin,resout,icntrl,icntrlw,iha,molresflag,altcol,inscol,
     -  ninsres,temp,rprox,irescount3,itemp1,hblimfac,angmin,iconfsel,
     -  numsel,nextconfsel,molsltlim,nmolslt,nmolsltnoion,0,minresflag,
     -  iqspaceask,1,iwriteatsym,radtodeg,tofac,0,ipbcinp,0,lentest_ok,
     -  maxconfsel,maxrepconf,pi,MAXRSD,MAXNEIG,MAXREC)
      stop
c-----(18) Convert sequences
9800  continue
      call convertseq(resnames,dssplab,segnames,inpfile,outfile,
     -  MAXRSD)
      stop
c-----(19) Unpack trajectory
9900  call unpacktraj(inptrajtyp,mmctrajtyp,inpcrdtyp,iotyp,
     -  inpcrdtyporg,n,nslt,naslv,iislvw,numres,numslv,resnamslv,iatnum,
     -  ifchrg,ixres,atnames,resnames,segnames,altcol,inscol,ninsres,
     -  marker,ireseq,iresnrestart,iresidrestart,c,rprox,cv,charge,
     -  ntitlin,title,nneig,nneiga,nhbneig,ineig,nhneig,nnneig,ncneig,
     -  nsneig,npneig,isegno,iresno,line,blankline,ifree,icntrl,innlist,
     -  mmtype,ibnd,index,indexn,indexo,molresflag,hblimfac,angmin,
     -  ioppbc,cell,ncell,ionefile,imodel,numsel,iconfsel,
     -  irescount3,itemp1,nclstmem,numconsec,incr_fileno,nextconfsel,
     -  iqspaceask,keeprem,radtodeg,ioverallconf,lentest_ok,maxconfsel,
     -  maxrepconf,MAXNEIG,MAXRSD,MAXREC,MAX2D)
      stop
c-----(20) Create residue trace PDB file
8100  if (inpcrdtyp .gt. ioins) then
        print *,'This input format does not have enough information'
        stop
      end if
      write (6,2071) outfile(1:namleno)
      write (iuout,2069)
      do il=1,ntitlin
        if (ispdb(inpcrdtyp) .gt. 0) then
          write (iuout,1000) line(il)(1:80)
        else if (ischarmm(iotyp) .eq. 1) then
          write (iuout,2070) line(il)(3:75)
        else if (inpcrdtyp .eq. iommod) then
          write (iuout,2070) line(1)(1:70)
          write (iuout,2070) line(1)(71:132)
        else
          write (iuout,2070) line(il)(1:73)
        end if
      end do
      if (nconfig .eq. 1) then
        call modrepats
        call askyn('Do you want to limit trace to CA-s',34,1,-1,icaonly,
     -    6,0)
      end if
      segnam='    '
      nresf=0
      do ir=1,nresslt
        call findat(iarep,ifres(ir),ilres(ir),line,index,
     -    irescol1,inamcol1,MAXREC)
c       print *,'ia,irf=',ia,irf
        atomnam='        '
        atomnam(1:nnamcol)=line(index(iarep))(inamcol1:inamcol2)
        resnam='        '
        resnam(1:nrescol)=line(index(iarep))(irescol1:irescol2)
        segnam(1:nsegcol)=line(index(iarep))(isegcol1:isegcol2)
        if (inpcrdtyp .ne. iobpdb) segnam(1:1)=abc(isegno(iarep))
        call leftadjustn(atomnam,rnn,8)
        if (icaonly .eq. 0 .or. rnn .eq. calpha(1:nnamcol)) then
          nresf=nresf+1
          call createrec(ansline,inpcrdtyp,iobpdb,c(1,iarep),c(2,iarep),
     -      c(3,iarep),' ',' ',atomnam,resnam,segnam,nresf,nresf,nresf,
     -      chnam,potnam,1.0,1.0,1,1,'  ',nneig(iarep),ineig(1,iarep),
     -      nconfig,ibnd,0,blankline)
          write (iuout,1000) ansline(1:80)
          indexn(nresf)=iarep
        end if
      end do
      if (nconfig .eq. 1)
     -  call askyn('Do you want CONECT records written in the PDB file',
     -    50,1,-1,iwritecon,0,0)
      if (iwritecon .eq. 1) then
        do ir=2,nresf
          iat=indexn(ir)
          iatprev=indexn(ir-1)
          if (isegno(iat) .eq. isegno(iatprev))
     -      write (iuout,1110) ir,ir-1
        end do
      end if
      write (iuout,1000) 'END'
      go to 9910
c-----(21) Extract the sequence
9620  call writeseq(inpfile,namleni,numres,resnames,dssplab,numslv,line,
     -    index,inpcrdtyp,title,nslt,n,1,MAXRSD,MAXREC)
      go to 9910
c-----(22),(23) Create a Grasp .crg  or UGBD .   file
9630  namleno=0
      call openfile(iuout,0,'output',6,'new',outfile,namleno,notfnd,
     -  0,1,1,0,0)
      write (6,1000) RTFtyps
      ncol=5
      if (iruntyp .eq. 23) ncol=7
      nconvdat=0
      call getrtfdat(nconvdat,ncol,iconvtyp,ntypedat)
      if (iruntyp .eq. 22)
     -   call writegrasp(iuout,nconvdat,analfile,namlena)
      if (iruntyp .eq. 23)
     -   call writeUHBD(iuout,nconvdat,analfile,namlena,iconvtyp)
      stop
c-----(24) Generate torsion information
9040  call settorslim(itemp3,molsltlim,nslt,nmolslt,iresno,segid4)
      call torsinp(nconfig,inpcrdtyp,itform,extnam1,analfile,inpfile,
     -  namleni,line,index,n,nslt,c,iatnum,ifchrg,nneig,nneiga,nhbneig,
     -  ineig,nhneig,nnneig,ncneig,nsneig,npneig,isegno,ixres,ibnd,
     -  indexo,naslv,islvw,innlist,molresflag,hblimfac,angmin,irescol1,
     -  irescol2,iresncol1,iresncol2,inamcol1,inamcol2,itemp3,itemp1,
     -  itemp2,pi,radtodeg,maxrepconf,MAXNEIG,maxbox,MAXRSD,MAXREC)
      go to 9910
c-----(25) Generate cell information
9050  call pbctype(ioppbc,npbc,ixyzhex,1)
      if (ioppbc .lt. 1 .or. ioppbc .gt. 9) then
        print *,'This cell type (',ioppbc,') has no neighbor cell info'
        go to 9910
      end if
      call pbcsize(ioppbc,edge,npbc)
      ncellfn=13
      analfile(1:ncellfn)='imagecell.pdb'
      call openfile(10,0,'image cell',10,'new',analfile,ncellfn,
     -  notfnd,0,1,1,0,0)
      print *,'Cell centers for ',cellname(ioppbc),
     -  ' PBC will be written to file ',analfile(1:ncellfn)
      nvertfn=15
      outfiletmp(1:nvertfn)='pbcvertices.pdb'
      call openfile(20,0,'cell vertex',11,'new',outfiletmp,nvertfn,
     -  notfnd,0,1,1,0,0)
      print *,'Cell vertices for ',cellname(ioppbc),
     -  ' PBC will be written to file ',outfiletmp(1:nvertfn)
      call crorgn(edge,edge_gen,ioppbc,3,ncell,cell,cellalt,
     -  ixyzhex,rinscr,rcirc)
      call prtcell(ioppbc,edge,edge_gen,r,vol,nw,1)
      call transrot(cell,c2,atw,temp,rot,xyzmin,c0,edge_gen,molsltlim,
     -  nsegslt,nsegslt,minresflagx,ncell,ncell,1,npbc,ixyzhex,nneig,
     -  ineig,iatnum,molresflag,ixres,atnames,latnam,resnames,lresnam,
     -  iresno,indexn,indexo,indexa,0,0,0,pi,MAXRSD,MAXNEIG,MAXREC)
c     Print cell centers
      do ia=1,ncell
        do k=1,3
          c8(k)=cell(k,ia)
        end do
        call writepdbd(10,c8,ia,1,'CELL',pbcres(ioppbc),'P',1.0,0.0)
      end do
c     write (10,1101) ((cell(k,i),k=1,3),i=1,ncell)
      iclr=1
      call zeroit(centinp,3)
      call drawpbc(edge,edge_gen,ioppbc,ixyzhex,centinp,0,
     -  1,rot,1.8,20)
      close(10)
      close(20)
      go to 91
c-----(26) Generate a rectangle PDB file
9060  call getxyz('',0,' coordinates of the box center',30,0.0,centinp,
     -  0,0)
      call askyn('Do you want to create a cube',28,1,1,icube,0,0)
      if (icube .eq. 1) then
        call getreal('Edge length of the cube',23,999999.0,edg,1,0)
        do k=1,3
          edge(k)=edg
        end do
      else
        call getxyz('Edge of the box in the ',23,' direction',10,
     -    999999.0,edge,0,0)
      end if
      do k=1,3
        xyzminmax(k,1)=centinp(k)-edge(k)/2.0
        xyzminmax(k,2)=centinp(k)+edge(k)/2.0
      end do
      lanalfile=7
      analfile(1:lanalfile)='box.pdb'
      call openfile(10,0,'PDB box',70,'new',analfile,lanalfile,
     -  notfnd,0,1,1,0,0)
      print *,'PDB file of the box  will be written to file ',
     -  analfile(1:lanalfile)
      ia=0
      do ix=1,2
        do iy=1,2
          do iz=1,2
            ia=ia+1
            do k=1,3
              vertex(1,ia)=xyzminmax(1,ix)
              vertex(2,ia)=xyzminmax(2,iy)
              vertex(3,ia)=xyzminmax(3,iz)
            end do
            write (10,2132) ia,1,(vertex(k,ia),k=1,3)
          end do
        end do
      end do
      do i=1,8
        do j=i+1,8
          nd=0
          do k=1,3
            if (vertex(k,i) .ne. vertex(k,j)) nd=nd+1
          end do
          if (nd .eq. 1) write (10,1110) i,j
        end do
      end do
      close(10)
      go to 91
c-----(27) Modify (mutate/add) atoms
9610  call modify(inpcrdtyp,c,n,nslt,line,index,iatnum,nneig,ineig,
     -  isegno,iresno,charge,icharges,natomadd,lineread,pi,MAXNEIG,
     -  MAXREC)
      go to 999
c-----(28) Replace coordinates
9640  if (n .lt. nref) write (6,2090) 'more',nref,n
      if (n .gt. nref) write (6,2090) 'less',nref,n
      if (n .ne. nref)  call askstop(0)
      if (ioverlay .eq. 0) then
c       Just copy
        if (anscnv .eq. 'n') call trnsfr(c,co,3*nref)
        if (anscnv .eq. 'h') call trnsfr(charge,cv,nref)
        nerr=0
        do ia=1,min0(n,nref)
          if (iatnum(ia) .ne. indexa(ia)) then
            write (6,2091) ia,iatnum(ia),indexs(ia)
            nerr=nerr+1
          end if
        end do
        if (nerr .eq. 0) print *,'All atomic numbers matched'
        if (nerr .gt. 0)  call askstop(0)
      else if (anscnv .eq. 'o') then
c       Overlay
        call askyn('Do you want to select the atoms to match',40,1,1,
     -    iselect,45,5)
        if (iselect .eq. 1) then
          call select(line,nrecdel,idcol,asterisk,n,nslt,index,ixres,
     -      isegcol1,isegcol2,iseqncol1,iseqncol2,inamcol1,inamcol2,
     -      irescol1,irescol2,iqcol1,iqcol2,charge,iatnum,nneig,ineig,
     -      indexdel,0,MAXNEIG,MAXREC)
          call masktolist(indexa,indexdel,n,nmatch,0)
          call askyn(
     -      'Do you want to apply the selection to both structures',53,
     -      1,1,iboth,00,0)
          if (iboth .eq. 1) then
            call bestoverlay(nmatch,indexa,indexa,co,c,atw,0.d0,c1,c2,
     -        temp,rot,c0,crm,0,0.001,0,MAXREC)
          else
            call indexit(indexn,1,nmatch,0)
            print *,'NMATCH=',nmatch
            print *,'line1=',line(index(1))(1:30)
c           write (77,7891) (i,indexa(i),
c    -       line(index(indexa(i)))(inamcol1:inamcol2),
c    -       line(index(indexa(i)))(irescol1:irescol2),i=1,nmatch)
c7891      format(i5,' indexa=',i5,' name=',a,' res=',a)
            call bestoverlay(nmatch,indexn,indexa,co,c,atw,0.d0,c1,c2,
     -        temp,rot,c0,crm,0,0.001,0,MAXREC)
          end if
        else
          call indexit(indexa,1,nslt,0)
          call bestoverlay(nslt,indexa,indexa,co,c,atw,0.d0,c1,c2,
     -      temp,rot,c0,crm,0,0.001,0,MAXREC)
        end if
        write (6,2029) nmatch
        call shiftmol(c,n,crm,c1,-1.0)
        call rotate_c(c1,n,rot,c2,'OVERLAY',7)
        call shiftmol(c2,n,c0,c,+1.0)
      end if
      go to 999
c-----(29) Write Charmm RTF residue record(s)
9650  iqok=0
      if (iqcol2 .ge. iqcol1) iqok=1
      ipotok=0
      if (ipotcol2 .ge. ipotcol1) ipotok=1
c     namin(ia): atomname; resin(ia): pf name
      do ia=1,nslt
        namin(ia)=line(index(ia))(inamcol1:inamcol2)
        if (ipotok .eq. 1) then
          namout(ia)='    '
          namout(ia)= line(index(ia))(ipotcol1:ipotcol2)
          if (inpcrdtyp .eq. ioins) resin(ia)=namout(ia)
        else
          namout(ia)='????'
        end if
        if (iqok .eq. 1) then
          read (line(index(ia))(iqcol1:iqcol2),*) charge(ia)
        else
          charge(ia)=0.0
        end if
      end do
      do ir=1,nresslt
        resnames(ir)='     '
        ia=ifres(ir)
        resnames(ir)(1:nrescol)=line(index(ia))(irescol1:irescol2)
      end do
      resnamp='    '
      do while (resnamp .ne. 'DONE')
        call getname(resnamp,len,
     -    'Residue name to print (type DONE or to quit)',41,4,'',0,1,0,
     -    0)
        if (len .eq. 0) resnamp='DONE'
        ir=1
        do while (resnamp .ne. resnames(ir)(1:4)
     -    .and. ir .lt. nresslt)
          ir=ir+1
        end do
        if (resnamp .eq. resnames(ir)(1:4)) then
          if (inpcrdtyp .eq. ioins) then
c           Insight .car file - convert types
            call cvffconv(cvffname,charmmname,resin(ifres(ir)),
     -        indexn(ifres(ir)),namout(ifres(ir)),ilres(ir)-ifres(ir)+1,
     -        indexa,ncvfftyp,iambig,indexs,nambig,noconv,0,maxcvfftyp)
            call refineconv(ilres(ir)-ifres(ir)+1,namout(ifres(ir)),
     -        namin,iatnum,indexa,indexn,nneig,ineig,nhneig,itemp1,
     -        indexo,MAXNEIG,maxcvfftyp,MAXREC)
c           indexn,indexo,indexa,indexs: icvff,iringtyp,iused,lstambig
            write (6,2031) noconv,nambig
            do i=1,maxcvfftyp
              if (indexa(i) .gt. 0 ) then
                if (iambig(i) .eq. 0) then
                  write (6,2032) i,cvffname(i),charmmname(i)
                else
                  write (6,2033) i,cvffname(i),charmmname(i)
                end if
              end if
            end do
          end if
          if (noconv .gt. 0) write (6,2034)
          call printrtfres(ifres(ir),ilres(ir),nneig,ineig,resnamp,
     -      charge,namin,namout,indexs,iqok,ipotok,iuout,outfile,
     -      namleno,MAXNEIG,MAXREC)
          call checkdupnames(namin(ifres(ir)),4,ilres(ir)-ifres(ir)+1,
     -      'RTF atom names',14,idup)
          if (idup .eq. 0) print *,'No duplicate names found - OK'
        else if (resnamp .ne. 'DONE') then
          print *,'No residue named ',resnamp,' was found'
        end if
      end do
      close (iuout)
      stop
c-----(30) Cluster atoms
9660  nresfound=iresno(nslt)-iresno(1)+1
      call getrange(ifdenat,1,ildenat,nslt,incrden,1,
     -  'atoms to use for density calculation',36,nslt,115)
      if (nresfound .gt. 1 .and. incrden .gt. 1) then
        call askyn(
     -    'Do you want to increment by residue (instead of atoms)',54,
     -    1,-1,iresinc,0,0)
      else
        iresinc=0
      end if
      if (iresinc .eq. 0) then
c       Incrementing by atoms
        if ((ildenat-ifdenat+1)/incrden+1 .gt. MAX2D) then
          write (6,2096) MAX2D,MAX2D
          go to 9660
        end if
        na=0
        do ia=ifdenat,ildenat,incrden
          na=na+1
          call trnsfr(c2(1,na),c(1,ia),3)
          froccin(na)=froccin(ia)
          charge(na)=charge(ia)
        end do
      else
c       Incrementing by residues
        na=0
        nr=0
        iresprev=0
        do ia=ifdenat,ildenat
          iresia=iresno(ia)
          if (iresia .ne. iresprev) then
            nr=nr+1
            iresprev=iresia
          end if
          if (mod(nr,incrden) .eq. 1) then
            na=na+1
            if (na .gt. MAX2D) then
              write (6,2096) MAX2D,MAX2D
              go to 9660
            else
              call trnsfr(c2(1,na),c(1,ia),3)
              froccin(na)=froccin(ia)
              charge(na)=charge(ia)
            end if
          end if
        end do
      end if
      print *,'Extracted ',na,' atoms for clustering'
      call pairdistconf(c2,na,rmsdmn,rmsdmx)
      rmsdmin=sqrt(rmsdmn)
      rmsdmax=sqrt(rmsdmx)
      write (6,2097) rmsdmin,rmsdmax
      write (iuout,2097) rmsdmin,rmsdmax
      call indexit(indexs,1,na,0)
      call clusterdistr(na,iuout,temp,rmsdmn,rmsdmx,nhbdist,itemp1,
     -  itemp2,itemp3,itemp4,indexn,indexo,ncl,indexa,indexs,ixclst,
     -  itemp5,value,ifa_s,ila_s,ih,cv,0.0,rdclust,etotsaved,0,'RMSD',
     -  4,1,1,irepav,irepmx,irepeng,irepkm,engcl,c2,co,c1,na,0,iclstyp,
     -  iwt,0,label,500,0,idistprint,1,1,MAX2D,1)
      call printclusterext(c2,co,ncl,indexn,indexo,ixclst,na,
     -  iclstyp,outfile,namleno,iuout)
      close (iuout)
      call printclusterpdb(c2,froccin,charge,ncl,indexn,indexo,ixclst,
     -  title,na)
      call countsim(indexn,indexo,ixclst,ncl,0.0,rmsdsim,iorig,iuout,
     -  MAX2D)
      stop
c-----(31) Cluster data
9670  call read_distmat(10,n,distmin,distmax,indexdel,label,inpfile,
     -  iuout,MAX2D)
      call indexit(indexs,1,n,0)
      call clusterdistr(n,iuout,temp,distmin,distmax,nhbdist,itemp1,
     -  itemp2,itemp3,itemp4,indexn,indexo,ncl,indexa,indexs,ixclst,
     -  itemp5,value,ifa_s,ila_s,ih,cv,0.0,rdclust,etotsaved,0,
     -  'Distance',8,1,1,irepav,irepmx,irepeng,irepkm,engcl,c2,co,c1,1,
     -  0,iclstyp,iwt,0,label,500,1,idistprint,1,1,MAX2D,1)
      call countsim(indexn,indexo,ixclst,ncl,0.0,rmsdsim,iorig,iuout,
     -  MAX2D)
      stop
c-----(32) Separate MMC trajectory into fixed number trajectories
