c-----(38) Create a retro-inverso peptide from a normal one
9710  call getresrange(nsegm,indexs,isegno,ixres,iresno,ifres,
     -  'residue to use',14,nresslt,nslt,irefres1,irefres2,1,
     -  irefseg1,irefseg2,listrefres,nrefres,nrefrange,0,1,maxrsd,
     -  maxrec,111)
      if (irefseg1 .ne. irefseg2) then
        print *,'Peptide selected has to be in a single chain/segment'
        go to 9710
      end if
      chain1=line(index(ifres(irefres1)))(isegcol2:isegcol2)
      call retroinverso(c,ifres(irefres1),ilres(irefres2),nslt,iatnum,
     -  line,index,chain1,nsegm,iaincr,inamcol1,inamcol2,iresncol1,
     -  iresncol2,irescol1,irescol2,pi,ifail,nneig,ineig,itemp1,iuout,
     -  MAXNEIG,MAXREC)
      if (ifail .eq. 0) then
         write (6,1223) outfile(1:namleno)
      else
        call askyn('Do you want to try a different residue range',44,
     -    1,1,iretry,0,0)
        if (iretry .eq. 1) then
          go to 9710
        else
          close(iuout,status='delete')
        end if
      end if
