     -    nosort .eq. 0) then
        call sortatres(line,n,index,indexn,indexo,nhbneig,nneiga,
     -    iresno,nsneig,npneig,iresncol1,iresncol2,isegno,marker(iotyp),
     -    iotyp,nconfig,20,maxrepconf,MAXREC)
