      ireseqdef=-1
      if (nlpdel+natsdel+nfindel .gt. 0 .or.
     -    (ninsres .gt. 0 .and. ipredict .eq. 0)) ireseqdef=1
      if (ischarmm(iotyp) .eq. 1 .or. ispdb(iotyp) .gt. 0 .or.
     -    iotyp .eq. iommod .or. iotyp .eq. iommc) then
        call reseq(line,index,n,nslt,nsegm,iatfirst,iresfirst,
     -    iresidfirst,inpcrdtyp,iresno,resnames,numres,numslv,naslv,
     -    ninsres,resnamslv,iresnrestart,iresidrestart,nconfig,ireseq,
     -    ireseqdef,MAXRSD,MAXREC)
      end if
