        write (iuout,1000) 'END'
      end if
      if (nconfig .eq. 1 .and. numres-numslv .gt. 0)
     -  call writeseq(outfile,namleno,numres,resnames,dssplab,numslv,
     -  line,index,iotyp,title,nslt,n,0,MAXRSD,MAXREC)
