9910  if ((iruntyp .eq. iruntypclean .or. iruntyp .eq. 13 .or.
     -     (iruntyp .eq. 10 .and. itraj .eq. 0) .or. iruntyp .eq. 9 .or.
     -     iruntyp .eq. 15 .or. iruntyp .eq. 20 .or. iruntyp .eq. 39)
