     -      iocccol1,iocccol2,ichemcol1,ichemcol2,nrescol,nresncol,
     -      nsegcol,nnamcol,iofull)
          go to 990
        end if
      end if
      if (netot .gt. 0) then
        eav=etotsum/dfloat(netot)
        sd=dsqrt(etotsum2/dfloat(netot)-(etotsum/dfloat(netot))**2)
        write (6,2126) eav,sd,etotmin,etotmax,netot
