      subroutine findixsort(list,ifrst,nlist,intg,ix,itry)
      dimension list(nlist)
c     Find intg in the sorted list
      imin=ifrst
      imax=nlist
      ix=0
      if (imin .gt. imax) then
        itry=imin
        return
      end if
      do while (imax-imin .gt. 1)
        itry=(imax+imin)/2
        if (list(itry) .eq. intg) then
          ix=itry
          return
        else if (list(itry) .gt. intg) then
          imax=itry
        else
          imin=itry
        end if
      end do
      itry=imax
      if (imax .eq. imin) then
        if (list(itry) .eq. intg) ix=itry
      else
        if (list(imin).eq. intg) then
          ix=imin
        else if (list(imax).eq. intg) then
          ix=imax
        end if
      end if
      return
      end
      subroutine mergerec(line,index,indexo,isegno,iresncol1,iresncol2,
     -  m1,m2,n1,n2,iresn,ireso,isegn,n,maxrec)
c#    MMC routine 127 lstmod: 01/08/88
c*****Merge two sets for the sorting
      character* 132 line(maxrec)
      dimension index(n),indexo(n),isegno(n),iresn(n),ireso(n),isegn(n)
c     i,j: counters for the first and second part, resp.
c     k: counter for the merged array
c     print *,'m1,m2,n1,n2,n=',m1,m2,n1,n2,n
      i=m1
      j=n1
      k=1
20    if (i .le. m2 .and. j .le. n2) then
c       Pick value from first or second part, depending on the comparison
        if (isegno(i) .gt. isegno(j)) then
          idiff=1
        else if (isegno(i) .lt. isegno(j)) then
          idiff=-1
        else
c         Compare residue numbers since segment id's were identical
          call readint(line(index(i)),iresncol1,iresncol2,numi,2,1,
     -      irerr)
          call readint(line(index(j)),iresncol1,iresncol2,numj,2,1,
     -      irerr)
          if (numi .gt. numj) then
            idiff=1
          else
            idiff=0
          end if
        end if
        if (idiff .eq. 1) then
          iresn(k)=index(j)
          ireso(k)=indexo(j)
          isegn(k)=isegno(j)
          j=j+1
        else
          iresn(k)=index(i)
          ireso(k)=indexo(i)
          isegn(k)=isegno(i)
          i=i+1
        end if
        k=k+1
        go to 20
      else
        if (i .le. m2) then
c         Leftover in the first part
          do l=i,m2
            iresn(k)=index(l)
            ireso(k)=indexo(l)
            isegn(k)=isegno(l)
            k=k+1
          end do
        end if
        if (j .le. n2) then
c         Lefotver in the second part
          do l=j,n2
            iresn(k)=index(l)
            ireso(k)=indexo(l)
            isegn(k)=isegno(l)
            k=k+1
          end do
        end if
      end if
c     Move back merged data into index
      k=0
      do l=m1,n2
        k=k+1
        index(l)=iresn(k)
        indexo(l)=ireso(k)
        isegno(l)=isegn(k)
      end do
      return
      end
      subroutine mrgsrt(iout,indexx,value,n,ifa,ila,itemp,temp,maxt)
c*****Sort the indexx and value array in the order of increasing value(i)
      dimension indexx(n),value(n)
      dimension ifa(maxt),ila(maxt),itemp(maxt),temp(maxt)
c     print *,'MRGSRT n,maxt=',n,maxt
      call mrglimtst(iout,n,maxt,ireturn)
      if (ireturn .eq. 1) return
      nn=n
      call indexit(ifa,1,n,0)
      call indexit(ila,1,n,0)
c     Merge pairs of intervals
11    nnpair=nn/2
      l=1
      do i=1,nnpair
        call mergelst(indexx,value,ifa(l),ila(l),ifa(l+1),ila(l+1),
     -    itemp,temp,maxt)
        ifa(i)=ifa(l)
        ila(i)=ila(l+1)
        l=l+2
      end do
      if (2*nnpair .ne. nn) then
c       Take care of last (odd) interval
        call mergelst(indexx,value,ifa(nnpair),ila(nnpair),ifa(nn),
     -    ila(nn),itemp,temp,maxt)
        ila(nnpair)=ila(nn)
      end if
      nn=nnpair
      if (nn .gt. 1) go to 11
      return
      end
      subroutine mergelst(indexx,value,m1,m2,n1,n2,ires,res,maxt)
c#    MMC routine 263 lstmod: 06/23/97
c*****Merge two sets for the sorting
      dimension indexx(maxt),value(maxt),ires(maxt),res(maxt)
c     i,j: counters for the first and second part, resp.
c     k: counter for the merged array
      i=m1
      j=n1
      k=1
20    if (i .le. m2 .and. j .le. n2) then
c       Pick value from first or second part, depending on value
        if (value(i) .le. value (j)) then
          res(k)=value(i)
          ires(k)=indexx(i)
          i=i+1
          go to 40
        end if
        res(k)=value(j)
        ires(k)=indexx(j)
        j=j+1
40      k=k+1
        go to 20
      end if
      if (i .le. m2) then
c       Leftover in the first part
        do l=i,m2
          res(k)=value(l)
          ires(k)=indexx(l)
          k=k+1
        end do
      end if
      if (j .le. n2) then
c       Leftover in the second part
        do l=j,n2
          res(k)=value(l)
          ires(k)=indexx(l)
          k=k+1
        end do
      end if
c     Move back merged data into index, value
      k=0
      do l=m1,n2
        k=k+1
        indexx(l)=ires(k)
        value(l)=res(k)
      end do
      return
      end
      subroutine mrgsrti(iout,indexx,ivalue,n,ifa,ila,itemp1,itemp2,
     -  maxt)
c#    MMC routine 262 lstmod: 10/17/97
c*****Sort the indexx and ivalue array in the order of increasing ivalue(i)
      dimension indexx(n),ivalue(n)
      dimension ifa(maxt),ila(maxt),itemp1(maxt),itemp2(maxt)
      call mrglimtst(iout,n,maxt,ireturn)
      if (ireturn .eq. 1) return
c     Individual intervals consist of single elements
      nn=n
c     print *,'MRGST n,maxt=',n,maxt
      call indexit(ifa,1,n,0)
      call indexit(ila,1,n,0)
c     Merge pairs of intervals
11    nnpair=nn/2
      l=1
      do i=1,nnpair
        call mergelsti(indexx,ivalue,ifa(l),ila(l),ifa(l+1),ila(l+1),
     -    itemp1,itemp2,maxt)
        ifa(i)=ifa(l)
        ila(i)=ila(l+1)
        l=l+2
      end do
      if (2*nnpair .ne. nn) then
c       Take care of last (odd) interval
        call mergelsti(indexx,ivalue,ifa(nnpair),ila(nnpair),ifa(nn),
     -    ila(nn),itemp1,itemp2,maxt)
        ila(nnpair)=ila(nn)
      end if
      nn=nnpair
      if (nn .gt. 1) go to 11
      return
      end
      subroutine mergelsti(indexx,ivalue,m1,m2,n1,n2,ires1,ires2,maxt)
c#    MMC routine 263 lstmod: 06/23/97
c*****Merge two sets for the sorting
      dimension indexx(maxt),ivalue(maxt),ires1(maxt),ires2(maxt)
c     i,j: counters for the first and second part, resp.
c     k: counter for the merged array
      i=m1
      j=n1
      k=1
20    if (i .le. m2 .and. j .le. n2) then
c       Pick ivalue from first or second part, depending on ivalue
        if (ivalue(i) .le. ivalue (j)) then
          ires2(k)=ivalue(i)
          ires1(k)=indexx(i)
          i=i+1
          go to 40
        end if
        ires2(k)=ivalue(j)
        ires1(k)=indexx(j)
        j=j+1
40      k=k+1
        go to 20
      end if
      if (i .le. m2) then
c       Leftover in the first part
        do l=i,m2
          ires2(k)=ivalue(l)
          ires1(k)=indexx(l)
          k=k+1
        end do
      end if
      if (j .le. n2) then
c       Leftover in the second part
        do l=j,n2
          ires2(k)=ivalue(l)
          ires1(k)=indexx(l)
          k=k+1
        end do
      end if
c     Move back merged data into index, value
      k=0
      do l=m1,n2
        k=k+1
        indexx(l)=ires1(k)
        ivalue(l)=ires2(k)
      end do
      return
      end
