        end do
9031    if (ifound .eq. 0) then
          if (nanos .lt. 15) then
            nanos=nanos+1
            ialist(nanos)=iatnum(i)
            ixlist(iatnum(i))=nanos
            icatlist(nanos)=ifchrg(i)
