     -  0.64,1.90,0.67,0.0, 1.19,2.80,1.71,0.0,
     -  0.12,-1.60,-0.14,0.0, -0.18,-0.80,-0.46,0.0,
     -  -0.05,-0.70,-0.25,0.0, 0.81,-0.90,2.09,0.0,
     -  0.26,-1.30,0.71,0.0, 1.08,4.20,0.46,0.0,
